package 数组;

public class 定义数组1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] arr;								//声明变量
		arr=new int[3];							//创建数组对象
		System.out.println("arr=[0]="+arr[0]);  //访问第一个数组
		System.out.println("arr=[1]="+arr[1]);  //访问第二个数组
		System.out.println("arr=[2]="+arr[2]);  //访问第三个数组
		System.out.println("数组的长度为："+arr.length);//打印数组长度
	}
}
		
		